mapper.select:
select b.b_title, b.userid, b.b_date i.img_path 
from board b, image i where b.b_code=
 and b.b_loc_category=#{keyword} and  b.b_idx=i.img_code order by b.b_date desc;

select b.b_title, b.userid, b.b_date i.img_path 
from board b, image i where b.b_code=
 and b.b_sub_category=#{keyword} and  b.b_idx=i.img_code order by b.b_date desc;

select * from board b, image i
 where b.b_code=
  and  b.b_idx=i.img_code order by b.b_date desc;
